//import { StatusBar } from "expo-status-bar";
//import { NavigationContainer } from "@react-navigation/native";
import { useEffect } from "react";
import { StyleSheet, View, Text, ImageBackground, Image } from "react-native";

//var ws = new WebSocket("ws://10.0.2.2:8888/GetActiveComms");
//var ws = new WebSocket("ws://192.168.0.101:8888/GetActiveComms");

function HomeScreen(props) {
  useEffect(() => {
    var ws = new WebSocket("ws://192.168.0.101:4649/Echo");

    ws.onopen = () => {
      // connection opened
      ws.send("something"); // send a message
      console.log("Send: something");
      alert("Send: something");
    };

    ws.onerror = (e) => {
      // an error occurred
      console.log("Error: " + e.message);
      alert("Error: " + e.message);
    };

    ws.onclose = (e) => {
      // connection closed
      console.log("Ws closed: " + e.code + " " + e.reason);
      alert("Ws closed: " + e.code + " " + e.reason);
    };

    ws.onmessage = (e) => {
      // a message was received
      console.log("Data received: " + e.data);
      alert("Data received: " + e.data);
    };
  });
  //ctrl + space to look for values in properties
  return (
    <ImageBackground
      source={{
        uri: "https://picsum.photos/200/300",
      }}
      style={styles.background}
    >
      <View style={styles.logoContainer}>
        <Image
          style={styles.logo}
          source={{
            width: 100,
            height: 150,
            uri: "https://picsum.photos/200/300",
          }}
        />
        <Text>TestText</Text>
      </View>

      <View style={styles.loginButton}></View>
      <View style={styles.registerButton}></View>
    </ImageBackground>
  );
}

const styles = StyleSheet.create({
  background: {
    flex: 1,
    justifyContent: "flex-end",
    alignItems: "center",
  },
  loginButton: {
    backgroundColor: "blue",
    width: "100%",
    height: 70,
  },
  logo: {},
  logoContainer: {
    position: "absolute",
    top: 70,
    alignItems: "center",
  },
  registerButton: {
    backgroundColor: "gold",
    width: "100%",
    height: 70,
  },
});

export default HomeScreen;
